// This function transforms the stock data JSON, focusing only on the 7 most recent time series entries.
// It now includes the original date and symbol fields, plus the merged symbol_date_id field,
// along with stock data.
function transformStockData(jsonData) {
  const transformedData = [];
  const timeSeries = jsonData["Time Series (Daily)"];
  const symbol = jsonData["Meta Data"]["2. Symbol"]; // Get the symbol from Meta Data

  // Get all dates from the time series and sort them in descending order (most recent first)
  const dates = Object.keys(timeSeries).sort((a, b) => new Date(b) - new Date(a));

  // Take only the 7 most recent dates
  const recentDates = dates.slice(0, 7);

  // Iterate over only the 7 most recent dates
  for (const date of recentDates) {
    const dailyData = timeSeries[date];
    transformedData.push({
      date: date, // Keeping the original date field
      symbol: symbol, // Keeping the original symbol field
      // New merged field: combines symbol and date for a unique identifier
      symbol_date_id: `${symbol}_${date}`,
      open: parseFloat(dailyData["1. open"]),
      high: parseFloat(dailyData["2. high"]),
      low: parseFloat(dailyData["3. low"]),
      close: parseFloat(dailyData["4. close"]),
      volume: parseInt(dailyData["5. volume"])
    });
  }
  return transformedData;
}

// n8n Function node typically receives data in the 'items' array.
// We assume the JSON data is in the first item's 'json' property.
const inputData = items[0].json;

// Transform the time series data, now limited to 7 most recent dates and including all fields
const newObjects = transformStockData(inputData);

// Prepare the output for n8n.
// Only the transformed time series data will be returned as individual items.
const outputItems = [];

// Add each transformed time series object as a separate item
newObjects.forEach(obj => {
  outputItems.push({ json: obj });
});

// Return the combined array of items.
return outputItems;
